#define MASTER_ADDR		0x60U
#define SLAVE_ADDR		0x01U

void delay(uint32_t ms)
{
	// 1s -> 16MHz
	// 1ms -> 1,6KHz
	uint32_t i = ms*1600;
	for (; i > 0; i--);
}

// Data transfer
// [7:4]	not used
// [3:1]	led number
// [0]		on or off

GPIO_Handle_t Led1, Led2, Led3, Led4, Led5;
I2C_Handle_t I2C2Handle;

// Led1		PD3
// Led2		PE2
// Led3		PE5
// Led4		PF7
// Led5		PG1

