// I2C2_SCL PF1
// I2C2_SDA PF0

void Init_I2C2(void)
{
	GPIO_Handle_t I2C_SCL, I2C_SDA;

	/*Note : Internal pull-up resistors are used */

	I2C_SCL.pGPIOx = GPIOF;
	I2C_SCL.GPIO_PinConfig.GPIO_PinMode = GPIO_MODE_ALTFN;
	I2C_SCL.GPIO_PinConfig.GPIO_PinOPType = GPIO_OP_TYPE_OD;
	I2C_SCL.GPIO_PinConfig.GPIO_PinPuPdControl = GPIO_PIN_NO_PUPD;
	I2C_SCL.GPIO_PinConfig.GPIO_PinAltFunMode = 4;
	I2C_SCL.GPIO_PinConfig.GPIO_PinSpeed = GPIO_SPEED_FAST;
	I2C_SCL.GPIO_PinConfig.GPIO_PinNumber = GPIO_PIN_1;
	GPIO_Init(&I2C_SCL);

	I2C_SDA.pGPIOx = GPIOF;
	I2C_SDA.GPIO_PinConfig.GPIO_PinMode = GPIO_MODE_ALTFN;
	I2C_SDA.GPIO_PinConfig.GPIO_PinOPType = GPIO_OP_TYPE_OD;
	I2C_SDA.GPIO_PinConfig.GPIO_PinPuPdControl = GPIO_PIN_NO_PUPD;
	I2C_SDA.GPIO_PinConfig.GPIO_PinAltFunMode = 4;
	I2C_SDA.GPIO_PinConfig.GPIO_PinSpeed = GPIO_SPEED_FAST;
	I2C_SDA.GPIO_PinConfig.GPIO_PinNumber = GPIO_PIN_0;
	GPIO_Init(&I2C_SDA);


	I2C2Handle.pI2Cx = I2C2;
	I2C2Handle.I2C_Config.I2C_ACKControl = I2C_ACK_ENABLE;
	I2C2Handle.I2C_Config.I2C_DeviceAddress = SLAVE_ADDR;
	I2C2Handle.I2C_Config.I2C_FMDutyCycle = I2C_FM_DUTY_2;
	I2C2Handle.I2C_Config.I2C_SCLSpeed = I2C_SCL_SPEED_FM400k;
	I2C_Init(&I2C2Handle);
}

int main(void)
{
	Init_GPIO_Leds();

	Init_I2C2();

	// Don't forget to enable I2C
	I2C_PControl(I2C2, ENABLE);

	// Enable ACKing
	I2C_ManageAcking(I2C2, I2C_ACK_ENABLE);

	// Enable interrupts
	I2C_IRQInterruptConfig(IRQ_NO_I2C2_EV, ENABLE);
	I2C_IRQInterruptConfig(IRQ_NO_I2C2_ER, ENABLE);

	// Enable callback
	I2C_SlaveEnableDisableCallbackEvents(I2C2, ENABLE);


	while (1);
}

void I2C2_EV_IRQHandler(void)
{
	I2C_EV_IRQHandling(&I2C2Handle);
}


void I2C2_ER_IRQHandler(void)
{
	I2C_ER_IRQHandling(&I2C2Handle);
}

uint8_t data = 0;

void I2C_ApplicationEventCallback(I2C_Handle_t *pI2CHandle,uint8_t AppEv)
{
	if(AppEv == I2C_EV_DATA_REQ)
	{
		// Master wants data
		//TODO not used in this example
	} else if (AppEv == I2C_EV_DATA_RCV)
	{
		// Master send data to slave, must read it
		data = I2C_SlaveReceiveData(I2C2);
	} else if (AppEv == I2C_ERROR_AF)
	{
		// Master send NACK, don't send more data
		//TODO not used in this example because slave isn't transmitting
	} else if (AppEv == I2C_EV_STOP)
	{
		// Master send data to slave and it has ended.
		uint8_t led = data >> 1;
		uint8_t on = data & 0x1U;
		switch (led)
		{
		case 0:
			GPIO_WriteToOutputPin(Led1.pGPIOx, Led1.GPIO_PinConfig.GPIO_PinNumber, on);
			break;
		case 1:
			GPIO_WriteToOutputPin(Led2.pGPIOx, Led2.GPIO_PinConfig.GPIO_PinNumber, on);
			break;
		case 2:
			GPIO_WriteToOutputPin(Led3.pGPIOx, Led3.GPIO_PinConfig.GPIO_PinNumber, on);
			break;
		case 3:
			GPIO_WriteToOutputPin(Led4.pGPIOx, Led4.GPIO_PinConfig.GPIO_PinNumber, on);
			break;
		case 4:
			GPIO_WriteToOutputPin(Led5.pGPIOx, Led5.GPIO_PinConfig.GPIO_PinNumber, on);
			break;

		}
	}
}
